# Define a class for a hash table
class HashTable:
    # Initialize the table with a given size and empty buckets
    def __init__(self, size):
        self.size = size
        self.table = [[] for i in range(size)]

    # Define the hash function to map keys to bucket indices
    def hash(self, key):
        return key % self.size

    # Insert a key-value pair into the table
    def insert(self, key, value):
        index = self.hash(key)
        bucket = self.table[index]
        for i, pair in enumerate(bucket):
            k, v = pair
            if k == key:
                bucket[i] = (key, value)
                return
        bucket.append((key, value))

    # Retrieve a value given a key
    def get(self, key):
        index = self.hash(key)
        bucket = self.table[index]
        for i, pair in enumerate(bucket):
            k, v = pair
            if k == key:
                return v
        raise KeyError('Key not found: {}'.format(key))

    # Remove a key-value pair from the table given a key
    def remove(self, key):
        index = self.hash(key)
        bucket = self.table[index]
        for i, pair in enumerate(bucket):
            k, v = pair
            if k == key:
                del bucket[i]
                return
        raise KeyError('Key not found: {}'.format(key))

    # Print the content of the hash table
    def print(self):
        for index in range(self.size):
            bucket = self.table[index]
            print('Bucket {}:'.format(index))
            for pair in bucket:
                print('\t', pair)

