        self.value = value
        self.next = None

class CircularLinkedList:
    def __init__(self):
        self.head = None

    def append(self, value):
        node = Node(value)
        if not self.head:
            self.head = node
            self.head.next = self.head
        else:
            current = self.head
            while current.next != self.head:
                current = current.next
            current.next = node
            node.next = self.head

    def remove(self, value):
        if not self.head:
            return
        if self.head.value == value:
            current = self.head
            while current.next != self.head:
                current = current.next
            current.next = self.head.next
            self.head = self.head.next
        else:
            current = self.head
            prev = None
            while current.next != self.head:
                prev = current
                current = current.next
                if current.value == value:
                    prev.next = current.next
                    current = current.next

    def print_list(self):
        if not self.head:
            return
        current = self.head
        while current.next != self.head:
            print(current.value, end=' -> ')
            current = current.next
        print(current.value)

